mapper.update:
INSERT 
	INTO 
		AKATSUKIRIN.CATEGORY
	(
		  SEQ
		, CODE
		, NAME
		, LINK
		, USEYN
		, CREATEUSER
		, CREATEDATE
		, UPDATEUSER
		, UPDATEDATE
	)
	VALUES 
	(
		  CATEGORY_SEQ.NEXTVAL
		, #{CODE }
		, #{NAME }
		, #{LINK }
		, #{USEYN }
		, #{USER_ID }
		, SYSTIMESTAMP
		, #{USER_ID }
		, SYSTIMESTAMP 
	)

UPDATE
	AKATSUKIRIN.CATEGORY
SET
	  CODE = #{CODE }
 	, NAME = #{NAME }
 	, LINK = #{LINK }
	, USEYN = #{USEYN }
	, UPDATEUSER = #{USER_ID }
	, UPDATEDATE = SYSTIMESTAMP 
	)
